import React, { Component } from "react";
import {Table, TableRow, TableCell, Input} from '@material-ui/core';
import { BarChart, Bar, XAxis, YAxis, Tooltip } from 'recharts';
const data = [{name: 'Page A', uv: 400, pv: 2400, amt: 2400}, ...rest];

function getIntroOfPage(label) {
    if (label === 'Page A') {
      return 'Page A is about men';
    } if (label === 'Page B') {
      return 'Page B is about women';
    } if (label === 'Page C') {
      return 'Page C is about women';
    } if (label === 'Page D') {
      return 'Page D is about household goods';
    } if (label === 'Page E') {
      return 'Page E is about food';
    } if (label === 'Page F') {
      return 'Page F is about baby food';
    }
  }
  
  function CustomTooltip({ payload, label, active }) {
    if (active) {
      return (
        <div className="custom-tooltip">
          <p className="label">{`${label} : ${payload[0].value}`}</p>
          <p className="intro">{getIntroOfPage(label)}</p>
          <p className="desc">Anything you want can be displayed here.</p>
        </div>
      );
    }
  
    return null;
  }

const renderBarChart = (
    <BarChart width={600} height={300} data={data}>
      <XAxis dataKey="name" stroke="#8884d8" />
      <YAxis />
      <Tooltip wrapperStyle={{ width: 100, backgroundColor: '#ccc' }} />
      <Legend width={100} wrapperStyle={{ top: 40, right: 20, backgroundColor: '#f5f5f5', border: '1px solid #d5d5d5', borderRadius: 3, lineHeight: '40px' }} />
      <CartesianGrid stroke="#ccc" strokeDasharray="5 5" />
      <Bar dataKey="uv" fill="#8884d8" barSize={30} />
    </BarChart>
);

class BoardList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            value : '',
            show_update: "block",
            show_delete: "none"
        }
    }

    // 수정버튼 클릭
    displayUpdateClick = (e) => {
        this.setState({
            show_update: "block",
            show_delete: "none"
        })
    }


    // 삭제버튼 클릭
    displayDeleteClick = (e) => {
        this.setState({
            show_update: "none",
            show_delete: "block"
        });
    }

   

    // Input : @material-ui/core에서 제공하는 input box로 라인형태로 제공
    // Input : html 태그
    render() {
        return(
            <div>
                {/* 리스트 결과 */}
                <Table>
                    <TableRow>
                        <TableCell width="20%" name="board_no" value={this.props.paykind}> {this.props.paykind} </TableCell>
                        <TableCell width="20%" name="board_title"> {this.props.price} </TableCell>
                        <TableCell width="20%" name="board_content"> {this.props.fee}</TableCell>
                    </TableRow>
                </Table>
                {renderBarChart}
            </div>
        );
    }


}

export default BoardList;
