import React, { Component } from "react";
import {Table, TableRow, TableCell, Input} from '@material-ui/core';
import moment from 'moment';  // 날짜타입


class BoardList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            value : '',
            show_update: "block",
            show_delete: "none"
        }
    }

    // 수정버튼 클릭
    displayUpdateClick = (e) => {
        this.setState({
            show_update: "block",
            show_delete: "none"
        })
    }


    // 삭제버튼 클릭
    displayDeleteClick = (e) => {
        this.setState({
            show_update: "none",
            show_delete: "block"
        });
    }

    // Input : @material-ui/core에서 제공하는 input box로 라인형태로 제공
    // Input : html 태그
    render() {
        return(
            <div>

                    {/* 리스트 결과 */}
                    <Table>
                        <TableRow>
                            <TableCell width="20%" name="board_no" value={this.props.kind}> {this.props.board_no} </TableCell>
                            <TableCell width="20%" name="board_title"> {this.props.price} </TableCell>
                            <TableCell width="20%" name="board_content"> {this.props.fee}</TableCell>
                        </TableRow>
                    </Table>
            </div>
        );
    }


}

export default BoardList;
