import React, { Component } from "react";
import {Table, TableRow, TableCell, Input} from '@material-ui/core';
import moment from 'moment';  // 날짜타입


class BoardList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            value : '',
            show_update: "block",
            show_delete: "none"
        }
    }

    // 수정버튼 클릭
    displayUpdateClick = (e) => {
        this.setState({
            show_update: "block",
            show_delete: "none"
        })
    }


    // 삭제버튼 클릭
    displayDeleteClick = (e) => {
        this.setState({
            show_update: "none",
            show_delete: "block"
        });
    }

    // Input : @material-ui/core에서 제공하는 input box로 라인형태로 제공
    // Input : html 태그
    render() {
        return(
            <div>
                <form action="/api/boardUpdate" method="POST">
                    { /* 수정, 삭제시 where절에서 key값을 비교하기 위해 hidden으로  board_no를 보관 */ }
                    <input type="hidden" name="board_no" value={this.props.board_no} />

                    {/* 리스트 결과 */}
                    <Table>
                        <TableRow>
                            <TableCell width="20%" name="board_no" value={this.props.board_no}> {this.props.board_no} </TableCell>
                            <TableCell width="20%" name="board_title"> {this.props.board_title} </TableCell>
                            <TableCell width="20%" name="board_content"> {this.props.board_content}</TableCell>
                            <TableCell width="20%" name="board_email"> {this.props.board_email}</TableCell>
                            <TableCell width="10%" name="board_regdate">{moment(this.props.board_regdate).format('YYYY-MM-DD hh:mm')}</TableCell>
                            <TableCell width="10%"><input type="button" class="btn" value="수정" onClick={this.displayUpdateClick} /></TableCell>
                            <TableCell width="10%"><input type="button" class="btn" value="삭제" onClick={this.displayDeleteClick} /></TableCell>
                        </TableRow>
                    </Table>

                    {/* 수정화면 => 수정시 where절에서 key값을 비교하기 위해 <input type="hidden" name="board_no" value={this.props.board_no}>*/}
                    <div className="list" style={{display: this.state.show_update}}> {/* 수정화면 보여주고 삭제화면 감추기 */}
                        <Table>
                            <TableRow>
                                <TableCell>
                                    <Input type="text" name="board_title" placeholder={this.props.board_title} />
                                </TableCell>
                                <TableCell>
                                    <Input type="textarea" name="board_content" placeholder={this.props.board_content} />
                                </TableCell>
                                <TableCell>
                                    <Input type="text" name="board_email" placeholder={this.props.board_email} />
                                </TableCell>
                                <TableCell>
                                    {moment(this.props.board_regdate).format('YYYY-MM-DD hh:mm')}
                                </TableCell>
                                <TableCell>
                                    <Input class="btn" type="submit" value="수정" color="primary" />
                                </TableCell>
                            </TableRow>
                        </Table>
                    </div>
                </form>

                {/* 삭제화면 */}
                <div className="delete" style={{display: this.state.show_delete}}> {/* 수정화면 보여주고 삭제화면 감추기 */}
                    <form action="/api/boardDelete" method="POST">
                        <input type="hidden" name="board_no" value={this.props.board_no} />
                        <Table>
                            <TableRow>
                                <TableCell></TableCell>
                                <TableCell></TableCell>
                                <TableCell>
                                    <span align="center">삭제하시겠습니까?</span>
                                </TableCell>
                                <TableCell>
                                    <input class="btn" type="submit" value="삭제" color="primary" />
                                </TableCell>
                            </TableRow>
                        </Table>
                    </form>
                </div>
            </div>
        );
    }


}

export default BoardList;
