import React, { Component } from "react";
import {Table, TableRow, TableCell, Input} from '@material-ui/core';
import { BarChart, Bar, XAxis, YAxis, Tooltip } from 'recharts';

const data = [
    {
      "name": "Page A",
      "uv": 4000,
      "pv": 2400
    },
    {
      "name": "Page B",
      "uv": 3000,
      "pv": 1398
    },
    {
      "name": "Page C",
      "uv": 2000,
      "pv": 9800
    },
    {
      "name": "Page D",
      "uv": 2780,
      "pv": 3908
    },
    {
      "name": "Page E",
      "uv": 1890,
      "pv": 4800
    },
    {
      "name": "Page F",
      "uv": 2390,
      "pv": 3800
    },
    {
      "name": "Page G",
      "uv": 3490,
      "pv": 4300
    }
  ]

class BoardList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            value : '',
            show_update: "block",
            show_delete: "none"
        }
    }

    // 수정버튼 클릭
    displayUpdateClick = (e) => {
        this.setState({
            show_update: "block",
            show_delete: "none"
        })
    }


    // 삭제버튼 클릭
    displayDeleteClick = (e) => {
        this.setState({
            show_update: "none",
            show_delete: "block"
        });
    }

   

    // Input : @material-ui/core에서 제공하는 input box로 라인형태로 제공
    // Input : html 태그
    render() {
        return(
            <div>
                {/* 리스트 결과 */}
                <Table>
                    <TableRow>
                        <TableCell width="20%" name="board_no" value={this.props.paykind}> {this.props.paykind} </TableCell>
                        <TableCell width="20%" name="board_title"> {this.props.price} </TableCell>
                        <TableCell width="20%" name="board_content"> {this.props.fee}</TableCell>
                    </TableRow>
                </Table>
                <BarChart width={730} height={250} data={data}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="name" />
                    <YAxis />
                    <Tooltip />
                    <Legend />
                    <Bar dataKey="pv" fill="#8884d8" />
                    <Bar dataKey="uv" fill="#82ca9d" />
                </BarChart>
            </div>
        );
    }


}

export default BoardList;
