import React, {Component} from 'react';
import Header from './Header';
import '../css/App.css';
import {Table, TableHead, TableRow, TableCell, AppBar} from '@material-ui/core';
import { BrowserRouter as Router} from 'react-router-dom';
import BoardList from './BoardList';
import moment from 'moment';

class MainList extends Component {
    // callApi : fetch를 통해 select -> response -> componentDidMount() res -> setState(board) -> state.board가 수정 -> BoardList에 props 형태로 전달
    state = {
        board: ''
    }

    // 생명주기   
    /*       
        componentDidAmont() 메서드 : 컴포넌트가 마운트 된 직후 호출되며, 데이터로딩과 같은 작업 등에 재정의
        예 ) List에 특정 item들을 출력하고자 할때 item에 들어갈 Data가 Server에 존재한다면 Server에서 읽어와서
        item 데이터가 state에 존재시 이 state에 데이터를 setting하는 작업
        마운트 즉 React 컴포넌트가 실제 DOM에 삽입된 후 서버로부터 데이터를 읽어와서 setState후 화면에 rerendering
    */
    componentDidMount() {
        this.callApi()
            .then(res => this.setState({board: res}))
            .catch(err => console.log(err));
            console.log("this.state.board ==>" + this.state.board);
    }

    /*
        async/await는 Promise를 더욱 쉽게 사용할 수 있도록 해주는 ES8(ES2017) 문법이다.
        이 문법을 사용하기 위해서 함수의 앞부분에 async 키워드를 추가하고,
        해당 함수 내부에서 Promise의 앞부분에 await 키워드를 사용한다.    

    */
    callApi = async() => {
        const response = await fetch('/api/boardList');  // await fetch('/url')로 json 형식으로 가져온 리스트(boardList)를 받아와서 response에 담는다.
        const body = await response.json();
        console.log("body ==> " + body);
        return body;
    }


    render() {
        return(
            <Router>
                <div display="flex" alignItems="center" justifyContent="center">
                    <AppBar position="static">
                            <div><Header /></div>
                    </AppBar> 
                    <br/>

                    <h3 align="center"> 게시판 목록 </h3>

                    <Table>
                            <TableHead>
                                <TableRow>
                                    <TableCell><b>No</b></TableCell>
                                    <TableCell><b>TITLE</b></TableCell>
                                    <TableCell><b>CONTENT</b></TableCell>
                                    <TableCell><b>EMAIL</b></TableCell>
                                    <TableCell><b>REGDATE</b></TableCell>
                                </TableRow>
                            </TableHead>
                    </Table>

                        {/* setState()에 의해 board 변수의 값이 select한 결과로 변경후, BoardList에 props 형태로 board_tbl 테이블의 게시판정보 데이터 전달 */}
                        { /* props로 전달할 값들은 반드시 대문자 컬럼 */}
                        {
                            this.state.board ? this.state.board.map((b, index) => {
                                return <BoardList key={index}
                                                    board_no={b.BOARD_NO}
                                                    board_title={b.BOARD_TITLE}
                                                    board_content={b.BOARD_CONTENT}
                                                    board_email={b.BOARD_EMAIL}
                                                    board_regdate={b.BOARD_REGDATE} />
                            })  : ''
                        }
                </div>
            </Router>    
        );
    }

}

export default MainList;
